    [AllowAnonymous]
    public class ForgotPassword : PageModel
    {
        private readonly UserManager<UserAccount> _userManager;
        private readonly IEmailSender _emailSender;
        private readonly ISendGridEmailService _sendGridEmailService;

        public ForgotPassword(UserManager<UserAccount> userManager, IEmailSender emailSender, ISendGridEmailService sendGridEmailService)
        {
            _userManager = userManager;
            _emailSender = emailSender;
            _sendGridEmailService = sendGridEmailService;
        }
        public void OnGet()
        {
        }

        [BindProperty]
        public InputModel Input { get; set; }

        public class InputModel
        {
            [Required]
            [EmailAddress]
            public string Email { get; set; }
        }

        public async Task<IActionResult> OnPostAsync()
        {
            if (ModelState.IsValid)
            {
                var user = await _userManager.FindByEmailAsync(Input.Email);
                if (user == null)
                {
                    // Don't reveal that the user does not exist or is not confirmed
                    return RedirectToPage("./ForgotPasswordConfirmation");
                }

                var subject = "Reset Password";

                var code = await _userManager.GeneratePasswordResetTokenAsync(user);
                code = WebEncoders.Base64UrlEncode(Encoding.UTF8.GetBytes(code));

                string domainName = Request.Scheme + "://" + Request.Host.Value;
                var url = $"{domainName}/Identity/Account/ResetPassword?code={code}";

                var message = GetHtmlMessage(url);

                await _emailSender.SendEmailAsync(Input.Email, subject, message);

                //await _sendGridEmailService.SendResetPasswordEmail(Input.Email, user.FirstName);

                return RedirectToPage("./ForgotPasswordConfirmation");
            }

            return Page();
        }
